        ctf.from_dict(
            {
                "defocus": 1,
                "cs": 2,
                "voltage": 300,
                "apix": 1.5,
                "bfactor": 0,
                "ampcont": 0.1,
            }
        )
        return_new = fu.ctf_1d(nx=20, ctf=ctf, sign=1, doabs=False)
