    def test_3d_img_negative_s(self):
        return_new = fu.cosinemask(
            im=IMAGE_3D, radius=-1, cosine_width=5, bckg=None, s=-10
        )
        return_old = oldfu.cosinemask(
            im=IMAGE_3D, radius=-1, cosine_width=5, bckg=None, s=-10
        )
        self.assertTrue(
            allclose(
                return_new.get_3dview(),
                return_old.get_3dview(),
                atol=TOLERANCE,
                equal_nan=True,
            )
        )


